var currentUrl = window.location.href;
var regex1 = /ja_inbox$/;
var regex2 = /all_assigments/;
var regex3 = /action=assignmentSubmit/;
var containsSubstring1 = regex1.test(currentUrl);
var containsSubstring2 = regex2.test(currentUrl);
var containsSubstring3 = regex3.test(currentUrl);
if (containsSubstring2 || containsSubstring1) {
 let redirectUrl=localStorage.getItem("redirectUrl");
 console.log('execute',redirectUrl);
 if(redirectUrl ){
 window.location.href = redirectUrl;
 localStorage.removeItem("redirectUrl");
 }
 if(containsSubstring1){
 window.location.href ="/jw/";
 }
}

function showFlash(){
 var flash = getUrlParam("flash");
 flash = decodeURIComponent(flash);
 if (flash) {
 window.FlashMessage.success(flash, {
 progress: true,
 interactive: true,
 timeout: 8000,
 appear_delay: 200,
 container: ".flash-container",
 theme: "default",
 classes: {
 container: "flash-container",
 flash: "flash-message",
 visible: "is-visible",
 progress: "flash-progress",
 progress_hidden: "is-hidden",
 },
 });
 var urlWithoutParam = removeURLParameter("flash");
 window.history.replaceState({}, document.title, urlWithoutParam);
 }
}

function showMessage(){
 var messageParam = getUrlParam("msg");
 messageParam = decodeURIComponent(messageParam);
 if (messageParam) {
 var urlWithoutParam = removeURLParameter("msg");
 window.history.replaceState({}, document.title, urlWithoutParam);

 }
}

function removeURLParameter(parameter) {
 var url = window.location.href;
 var urlParts = url.split("?");
 if (urlParts.length >= 2) {
 var prefix = encodeURIComponent(parameter) + "=";
 var params = urlParts[1].split(/[&;]/g);
 for (var i = params.length - 1; i >= 0; i--) {
 if (params[i].lastIndexOf(prefix, 0) !== -1) {
 params.splice(i, 1);
 }
 }
 url =
 urlParts[0] + (params.length > 0 ? "?" + params.join("&") : "");
 }
 return url;
}

