 public static boolean isWithinTwoMonths(String dateString) {
 // Check if the input date string is null or empty
 if (dateString == null || dateString.isEmpty()) {
 return false;
 }

 // Parse the input string into a LocalDate object
 LocalDate inputDate = LocalDate.parse(dateString, DateTimeFormatter.ISO_DATE);

 // Get the current date
 LocalDate currentDate = LocalDate.now();

 // Calculate the difference in months between the input date and the current date
 long monthsDifference = ChronoUnit.MONTHS.between(inputDate, currentDate);

 // Check if the difference is less than or equal to 2 months
 return monthsDifference <= 2;
 }

